import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:5001');

const ChatBox = ({ user }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    socket.on('receive_message', (data) => {
      setMessages((prev) => [...prev, data]);
    });

    return () => {
      socket.off('receive_message');
    };
  }, []);

  const sendMessage = () => {
    if (message.trim()) {
      const msgData = {
        sender: user.name,
        text: message.trim(),
        time: new Date().toLocaleTimeString(),
      };
      socket.emit('send_message', msgData);
      setMessages((prev) => [...prev, msgData]);
      setMessage('');
    }
  };

  return (
    <div className="fixed bottom-4 right-4 bg-white dark:bg-gray-800 p-4 rounded-lg shadow w-80 max-h-[60vh] overflow-y-auto">
      <h2 className="text-lg font-bold mb-2">Live Chat</h2>
      <div className="space-y-2 mb-2 max-h-40 overflow-y-auto">
        {messages.map((msg, idx) => (
          <div key={idx} className="text-sm">
            <strong>{msg.sender}:</strong> {msg.text}
            <span className="block text-xs text-gray-400">{msg.time}</span>
          </div>
        ))}
      </div>
      <div className="flex gap-2">
        <input
          type="text"
          placeholder="Type a message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="flex-1 p-1 border rounded dark:bg-gray-700 dark:text-white"
        />
        <button
          onClick={sendMessage}
          className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatBox;
