import React, { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';
import axios from 'axios';

const socket = io('http://localhost:5001');

const ChatBox = ({ user }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const userRef = useRef(user);

  useEffect(() => {
    userRef.current = user;
  }, [user]);

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const res = await axios.get('http://localhost:5001/api/chat');
        setMessages(res.data);
      } catch (err) {
        console.error('âŒ Failed to load chat history:', err);
      }
    };

    fetchHistory();

    socket.on('receive_message', (data) => {
      setMessages((prev) => [...prev, data]);
    });

    return () => {
      socket.off('receive_message');
    };
  }, []);

  const sendMessage = () => {
    const currentUser = userRef.current;
    if (!currentUser || !currentUser.name || !message.trim()) return;

    const msgData = {
      sender: currentUser.name,
      text: message.trim(),
      time: new Date().toLocaleTimeString(),
    };

    socket.emit('send_message', msgData);
    setMessages((prev) => [...prev, msgData]);
    setMessage('');
  };

  return (
    <div className="fixed bottom-4 right-4 z-50">
      {isOpen ? (
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg w-80 max-h-[70vh] flex flex-col">
          <div className="bg-blue-600 text-white p-2 rounded-t-lg flex justify-between items-center">
            <span className="font-semibold">Live Chat</span>
            <button onClick={() => setIsOpen(false)}>â€”</button>
          </div>

          <div className="p-3 overflow-y-auto flex-1 space-y-2">
            {messages.map((msg, idx) => (
              <div key={idx} className="text-sm">
                <strong>{msg.sender}:</strong> {msg.text}
                <div className="text-xs text-gray-400">{msg.time}</div>
              </div>
            ))}
          </div>

          <div className="flex p-2 border-t">
            <input
              type="text"
              placeholder="Type a message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              className="flex-1 p-1 border rounded dark:bg-gray-700 dark:text-white"
              onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
            />
            <button
              onClick={sendMessage}
              className="ml-2 bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
            >
              Send
            </button>
          </div>
        </div>
      ) : (
        <button
          onClick={() => setIsOpen(true)}
          className="bg-blue-600 text-white px-4 py-2 rounded-full shadow hover:bg-blue-700"
        >
          ðŸ’¬
        </button>
      )}
    </div>
  );
};

export default ChatBox;
