import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchUsers = async () => {
    try {
      const res = await axios.get('http://localhost:5001/api/users');
      setUsers(res.data);
      setLoading(false);
    } catch (err) {
      console.error('❌ Failed to fetch users:', err);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <div className="p-6 text-gray-900 dark:text-white">
      <h1 className="text-2xl font-bold mb-4">👥 Admin Panel – User Management</h1>

      {loading ? (
        <p>Loading users...</p>
      ) : (
        <table className="w-full border-collapse border border-gray-300 dark:border-gray-600 text-sm">
          <thead className="bg-gray-200 dark:bg-gray-700">
            <tr>
              <th className="border px-3 py-2">Name</th>
              <th className="border px-3 py-2">Email</th>
              <th className="border px-3 py-2">Role</th>
              <th className="border px-3 py-2">Chat Color</th>
              <th className="border px-3 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => {
  const [role, setRole] = useState(user.role);
  const [color, setColor] = useState(user.chatColor || '#3b82f6');

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5001/api/users/${user._id}/color`, { color });
      await axios.put(`http://localhost:5001/api/users/${user._id}/role`, { role });
      alert('✅ Updated!');
    } catch (err) {
      console.error('❌ Failed to update user:', err);
    }
  };

  return (
    <tr key={user._id}>
      <td className="border px-3 py-2">{user.name}</td>
      <td className="border px-3 py-2">{user.email}</td>
      <td className="border px-3 py-2">
        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}
          className="p-1 rounded border"
        >
          <option value="admin">admin</option>
          <option value="support">support</option>
          <option value="client">client</option>
        </select>
      </td>
      <td className="border px-3 py-2">
        <input
          type="color"
          value={color}
          onChange={(e) => setColor(e.target.value)}
          className="w-8 h-6 rounded border cursor-pointer"
        />
      </td>
      <td className="border px-3 py-2 text-center">
        <button
          onClick={handleSave}
          className="bg-green-600 text-white px-2 py-1 text-xs rounded hover:bg-green-700"
        >
          Save
        </button>
      </td>
    </tr>
  );
})}

          </tbody>
        </table>
      )}
    </div>
  );
};

export default AdminPanel;
