import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5001');

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [msg, setMsg] = useState('');

  useEffect(() => {
    socket.on('receive_message', (data) => {
      setMessages((prev) => [...prev, data]);
    });

    return () => {
      socket.off('receive_message');
    };
  }, []);

  const handleSend = () => {
    if (msg.trim()) {
      const messageObj = { text: msg };
      socket.emit('send_message', messageObj);
      setMessages((prev) => [...prev, messageObj]);
      setMsg('');
    }
  };

  return (
    <div className="bg-white p-4 rounded-xl shadow h-[400px] flex flex-col">
      <h2 className="text-lg font-semibold mb-2">Live Chat</h2>
      <div className="flex-1 overflow-y-auto border rounded p-2 mb-2">
        {messages.map((m, i) => (
          <div key={i} className="mb-1 text-sm bg-gray-100 p-2 rounded">
            {m.text}
          </div>
        ))}
      </div>
      <div className="flex gap-2">
        <input
          type="text"
          value={msg}
          onChange={(e) => setMsg(e.target.value)}
          placeholder="Type message..."
          className="flex-1 border rounded p-2 text-sm"
        />
        <button
          onClick={handleSend}
          className="bg-blue-600 text-white px-4 py-2 rounded text-sm"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatBox;
