import React, { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';
import axios from 'axios';

const socket = io('http://localhost:5001');

const ChatBox = ({ user }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const userRef = useRef(user);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    userRef.current = user;
  }, [user]);

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const res = await axios.get('http://localhost:5001/api/chat');
        setMessages(res.data);
      } catch (err) {
        console.error('❌ Failed to load chat history:', err);
      }
    };

    fetchHistory();

    socket.on('receive_message', (data) => {
      setMessages((prev) => [...prev, data]);
    });

    return () => {
      socket.off('receive_message');
    };
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = () => {
    const currentUser = userRef.current;
    if (!currentUser || !currentUser.name || !message.trim()) return;

    const msgData = {
      sender: currentUser.name,
      text: message.trim(),
      time: new Date().toLocaleTimeString(),
    };

    socket.emit('send_message', msgData);
    setMessages((prev) => [...prev, msgData]);
    setMessage('');
  };

  return (
    <div className="fixed bottom-4 right-4 z-50 text-sm font-sans">
      {isOpen ? (
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg w-80 max-h-[70vh] flex flex-col border border-gray-200 dark:border-gray-700">
          {/* Header */}
          <div className="bg-blue-600 text-white px-4 py-2 rounded-t-xl flex items-center justify-between">
  <div className="flex items-center gap-2 text-base font-semibold">
    <span>💬</span>
    <span>Live Chat</span>
  </div>
  <button
    onClick={() => setIsOpen(false)}
    className="text-white text-xl font-bold hover:text-gray-200 transition"
    title="Minimize"
  >
    &minus;
  </button>
</div>


          {/* Messages */}
          <div className="p-3 overflow-y-auto flex-1 space-y-2 scroll-smooth">
            {messages.map((msg, idx) => (
              <div key={idx}>
                <div>
                  <strong className="text-blue-600 dark:text-blue-400">{msg.sender}:</strong>{' '}
                  {msg.text}
                </div>
                <div className="text-xs text-gray-400 dark:text-gray-500">{msg.time}</div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          {/* Input */}
          <div className="flex p-2 border-t border-gray-200 dark:border-gray-700">
            <input
              type="text"
              placeholder="Type your message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
              className="flex-1 p-2 rounded border dark:bg-gray-700 dark:text-white focus:outline-none"
            />
            <button
              onClick={sendMessage}
              className="ml-2 px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Send
            </button>
          </div>
        </div>
      ) : (
        <button
          onClick={() => setIsOpen(true)}
          className="bg-blue-600 text-white px-4 py-2 rounded-full shadow hover:bg-blue-700"
        >
          💬
        </button>
      )}
    </div>
  );
};

export default ChatBox;
