import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = ({ setUser }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5001/api/auth/login', {
        email,
        password,
      });
      const fullUser = {
  id: res.data.user.id || res.data.user._id,
  name: res.data.user.name,
  role: res.data.user.role,
};
localStorage.setItem('user', JSON.stringify(fullUser));
      setUser(res.data.user); // ✅ React state թարմացում
      navigate('/dashboard');
    } catch (err) {
      alert('Login failed: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100 dark:bg-gray-900">
      <form onSubmit={handleLogin} className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow w-80 space-y-4">
        <h2 className="text-xl font-semibold text-center dark:text-white">Login</h2>
        <input
          type="email"
          placeholder="Email"
          className="w-full p-2 border rounded dark:bg-gray-900 dark:text-white"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          className="w-full p-2 border rounded dark:bg-gray-900 dark:text-white"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded">
          Login
        </button>
        <p className="text-sm text-center text-gray-600 dark:text-gray-400">
          Don't have an account? <a href="/register" className="text-blue-400 underline">Register</a>
        </p>
      </form>
    </div>
  );
};

export default Login;
