import React, { useEffect, useState } from 'react';
import axios from 'axios';

const roles = ['admin', 'support', 'client'];

const AdminPanel = () => {
  const [users, setUsers] = useState([]);

  // Բեռնում ենք բոլոր օգտատերերին
  useEffect(() => {
    axios.get('http://localhost:5001/api/users')
      .then(res => setUsers(res.data))
      .catch(err => console.error('Failed to fetch users', err));
  }, []);

  // Փոխում ենք role
  const handleRoleChange = async (userId, newRole) => {
    try {
      await axios.put(`http://localhost:5001/api/users/${userId}/role`, { role: newRole });
      setUsers(prev =>
        prev.map(u => u._id === userId ? { ...u, role: newRole } : u)
      );
    } catch (err) {
      console.error('Failed to update role', err);
    }
  };

  // Փոխում ենք չատի գույնը
  const handleColorChange = async (userId, color) => {
    try {
      await axios.put(`http://localhost:5001/api/users/${userId}/color`, { color });
      setUsers(prev =>
        prev.map(u => u._id === userId ? { ...u, color } : u)
      );
    } catch (err) {
      console.error('Failed to update color', err);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6">Admin Panel</h2>

      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2 border">Name</th>
            <th className="p-2 border">Email</th>
            <th className="p-2 border">Role</th>
            <th className="p-2 border">Chat Color</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user._id} className="border-t">
              <td className="p-2 border">{user.name}</td>
              <td className="p-2 border">{user.email}</td>
              <td className="p-2 border">
                <select
                  value={user.role}
                  onChange={e => handleRoleChange(user._id, e.target.value)}
                  className="border p-1 rounded"
                >
                  {roles.map(role => (
                    <option key={role} value={role}>{role}</option>
                  ))}
                </select>
              </td>
              <td className="p-2 border">
                <input
                  type="color"
                  value={user.color || '#000000'}
                  onChange={e => handleColorChange(user._id, e.target.value)}
                  className="w-10 h-6 p-0 border"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
