import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TicketList = ({ role }) => {
  const [tickets, setTickets] = useState([]);

  const fetchTickets = async () => {
    const res = await axios.get('http://localhost:5001/api/tickets');
    const user = JSON.parse(localStorage.getItem('user'));

    let filtered = res.data;

    if (role === 'client') {
      filtered = res.data.filter(t => t.createdBy._id === user.id);
    }
    if (role === 'support') {
      filtered = res.data.filter(t => t.assignedTo && t.assignedTo._id === user.id);
    }

    setTickets(filtered);
  };

  useEffect(() => {
    fetchTickets();
  }, []);

  return (
    <div className="bg-white p-4 rounded-xl shadow h-[400px] overflow-y-auto">
      <h2 className="text-lg font-semibold mb-2">Tickets</h2>
      <ul className="space-y-2">
        {tickets.map((ticket) => (
          <li key={ticket._id} className="p-3 border rounded">
            <p className="text-sm font-bold">{ticket.title}</p>
            <p className="text-xs text-gray-500">{ticket.status}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TicketList;
