import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5001');

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [msg, setMsg] = useState('');

  useEffect(() => {
    socket.on('receive_message', (data) => {
      setMessages((prev) => [...prev, data]);
    });

    return () => {
      socket.off('receive_message');
    };
  }, []);

  const handleSend = () => {
    if (msg.trim()) {
      const user = JSON.parse(localStorage.getItem('user'));
      const messageObj = {
        sender: user.name,
        text: msg,
      };
      socket.emit('send_message', messageObj);
      setMessages((prev) => [...prev, messageObj]);
      setMsg('');
    }
  };

  return (
    <div className="bg-white dark:bg-gray-800 p-4 rounded-xl shadow flex flex-col h-[400px]">
      <h2 className="text-lg font-semibold mb-2">Live Chat</h2>

      <div className="flex-1 overflow-y-auto border rounded p-2 mb-2 space-y-2">
        {messages.map((m, i) => (
          <div key={i} className="text-sm bg-gray-100 dark:bg-gray-700 p-2 rounded">
            <strong>{m.sender}: </strong> {m.text}
          </div>
        ))}
      </div>

      <div className="flex gap-2">
        <input
          type="text"
          value={msg}
          onChange={(e) => setMsg(e.target.value)}
          placeholder="Type message..."
          className="flex-1 border rounded p-2 text-sm dark:bg-gray-900 dark:text-white"
        />
        <button
          onClick={handleSend}
          className="bg-blue-600 text-white px-4 py-2 rounded text-sm hover:bg-blue-700"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatBox;
