import React, { useEffect, useState } from 'react';
import axios from 'axios';

const roles = ['admin', 'support', 'client']; // ամբողջական ցանկ

const AdminPanel = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    console.log(user.role)
    axios.get('http://localhost:5001/api/users')
      .then(res => setUsers(res.data))
      .catch(err => console.error('Error loading users:', err));
  }, []);

  const handleRoleChange = async (userId, newRole) => {
    try {
      const res = await axios.put(`http://localhost:5001/api/users/${userId}/role`, { role: newRole });
      setUsers(prevUsers =>
        prevUsers.map(user =>
          user._id === userId ? { ...user, role: res.data.role } : user
        )
      );
    } catch (err) {
      console.error('Error updating role:', err);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6">Admin Panel</h2>
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2 border">Name</th>
            <th className="p-2 border">Email</th>
            <th className="p-2 border">Role</th>
            <th className="p-2 border">Chat Color</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user._id} className="border-t">
              <td className="p-2 border">{user.name}</td>
              <td className="p-2 border">{user.email}</td>
              <td className="p-2 border">
                <select
                  value={user.role}
                  onChange={e => handleRoleChange(user._id, e.target.value)}
                  className="border p-1 rounded"
                >
                  {roles.map(role => (
                    <option key={role} value={role}>{role}</option>
                  ))}
                </select>
              </td>
              <td className="p-2 border">
                <input
                  type="color"
                  value={user.color || '#000000'}
                  onChange={e => {
                    axios.put(`http://localhost:5001/api/users/${user._id}/color`, { color: e.target.value })
                      .then(() => {
                        setUsers(prev =>
                          prev.map(u => u._id === user._id ? { ...u, color: e.target.value } : u)
                        );
                      });
                  }}
                  className="w-10 h-6 p-0 border"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
