import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const ActivationPage = () => {
  const { token } = useParams();
  const navigate = useNavigate();
  const [status, setStatus] = useState('pending'); // pending | success | error

  useEffect(() => {
    const activate = async () => {
      try {
        await axios.get(`http://localhost:5001/api/auth/activate/${token}`);
        setStatus('success');

        // Optional auto-redirect after 3 sec
        setTimeout(() => {
          navigate('/');
        }, 3000);
      } catch (err) {
        setStatus('error');
      }
    };

    activate();
  }, [token, navigate]);

  return (
    <div className="flex flex-col items-center justify-center h-screen text-center px-4">
      {status === 'pending' && <p>üîÑ Activating your account...</p>}
      {status === 'success' && (
        <>
          <h2 className="text-2xl font-bold text-green-600 mb-2">‚úÖ Account Activated!</h2>
          <p>You can now login. Redirecting...</p>
        </>
      )}
      {status === 'error' && (
        <>
          <h2 className="text-2xl font-bold text-red-600 mb-2">‚ùå Activation Failed</h2>
          <p>This link may be expired or already used.</p>
        </>
      )}
    </div>
  );
};

export default ActivationPage;
