import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchUsers = async () => {
    try {
      const res = await axios.get('http://localhost:5001/api/users');
      setUsers(res.data);
      setLoading(false);
    } catch (err) {
      console.error('‚ùå Failed to fetch users:', err);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <div className="p-6 text-gray-900 dark:text-white">
      <h1 className="text-2xl font-bold mb-4">üë• Admin Panel ‚Äì User Management</h1>

      {loading ? (
        <p>Loading users...</p>
      ) : (
        <table className="w-full border-collapse border border-gray-300 dark:border-gray-600 text-sm">
          <thead className="bg-gray-200 dark:bg-gray-700">
            <tr>
              <th className="border px-3 py-2">Name</th>
              <th className="border px-3 py-2">Email</th>
              <th className="border px-3 py-2">Role</th>
              <th className="border px-3 py-2">Chat Color</th>
              <th className="border px-3 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td className="border px-3 py-2">{user.name}</td>
                <td className="border px-3 py-2">{user.email}</td>
                <td className="border px-3 py-2">{user.role}</td>
                <td className="border px-3 py-2">
                  <div
                    className="w-5 h-5 rounded-full border"
                    style={{ backgroundColor: user.chatColor || '#ccc' }}
                    title={user.chatColor || 'default'}
                  ></div>
                </td>
                <td className="border px-3 py-2 text-center text-sm text-blue-600">‚úèÔ∏è Edit</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AdminPanel;
